import React, { useState }  from 'react';   
import { ImageView } from '../../components/imageView';
import { getRandomInt } from '../../store/mazzi';

// styles
import './quiz.scss';

interface QuizProps {  }

const Quiz: React.FC<QuizProps> = () => { 

  const [ prize , setPrize ] = useState<Array<number>>([]) 

  const creationPrize = () => {
    var num = getRandomInt(3)
    var newArray= []
    for(var i=0; i<3; i++ ){
      if(num===i){
        newArray[i]=1
      }else newArray[i]= 0
    }
    setPrize( newArray )
  }

  const imagePrize = [ {name:'capra', image:'quiz/capra.png'},{name:'auto', image:'quiz/auto.png',} ]

  return (
    <div className='pg-quiz'>  
      <h1>QUIZ</h1>
      <button onClick={creationPrize}> inizia </button>
      <div className='quiz-continer-box'>  
        { (prize.length > 0) && 
            prize.map( item => 
              <div className={'quiz-image'+imagePrize[item].name} >
                <h1>{item}</h1>
                <ImageView imageCard={imagePrize[item].image} nameCard={imagePrize[item].name}  className={imagePrize[item].name}/> 
              </div>
            )
        }
      </div>
    </div>
  );
};


export default React.memo(Quiz);
