        //     addHandsCard[i]?.cardHand.push( action.payload )
        //   }
        // }
      }
      // console.log(addHandsCard[0]?.dealer , 'addHandsCard')  
      // console.log(addHandsCard,'addHandsCard')  

      return addHandsCard
    }
  }
})


export const { setPlayer, setPlayerHand, setPlayerTable } = handsPlayersSlice.actions 

export default handsPlayersSlice.reducer
