import React, { FC } from 'react';
import { handCardsPlayerState } from '../../store/entities/handCardsPlayers';

// style
import './Player.scss';

interface PlayerProps { 
  dataPlayer:handCardsPlayerState
}

const Player: FC<PlayerProps> = ( {dataPlayer} ) => { 

const {
  turnOn,
  dealer,
  user,
  cardHand,
  cardHandDouble,
  valueHand, 
  valueHandDouble } = dataPlayer

  return (
    <>   
      <div className="pg-player" data-testid="player">
        <div className="player-info-container">
          <span className="player-turn"><h1>{turnOn}</h1></span>. 
          <span className="player-name"><h1>{user}</h1></span>. 
        </div>
      </div>
    </>
  );
};

export default React.memo(Player);
