import React, { useState }  from 'react';   
import { useSelector, useDispatch  } from  'react-redux';   
import { HandCards } from '../../components/HandCards';
import { TitlePage } from '../../components/TitlePage';
import { deleteCard, getRandomInt, setNewDecks } from '../../store/reducer/decksStore'; 
import { setPlayerHand } from '../../store/reducer/handCardsPlayersStore'; 
import { cardState } from '../../store/entities/deck';  
import { rootState } from '../..';  
import { handCardsPlayerState } from '../../store/entities/handCardsPlayers';


// styles
import './playStrategy.scss';

interface PlayStrategyProps {  }

/**
 * Components that returns a counter and redirect to main page
 */
const PlayStrategy: React.FC<PlayStrategyProps> = () => {  

  const dispatch = useDispatch(); 
  const [ card, setCard ] = useState<cardState>( {name:'start',value: 0 ,image:'cards/start.svg'}) ;  
  const decks = useSelector<rootState,Array<cardState>>(  state  => state.decks  ) ; 
  const playerHandCards = useSelector<rootState,Array<handCardsPlayerState>>(  state  => state.handsPlayers  ) ;

  const [ cardDealer, setCardDealer ] = useState<cardState[]>([])
  const [ cardPlayer, setCardPlayer ] = useState([])

  const creationDeck = () => {
    dispatch(setNewDecks()) 
  }

  const showCardHand = () => { 
    const positionCard = getRandomInt(decks.length) 
    cardDealer.push( decks[positionCard] )
    dispatch( deleteCard(positionCard) )   
  } 

  console.log(playerHandCards)


  return (
    <div className='pg-playStrategy'>   
      <TitlePage title='PlayStrategy' /> 
      <div className='container-handCard'> 
        <HandCards cardHand={cardPlayer} />
      </div> 
        <button className='showCardHand' onClick={showCardHand} >remove card</button>
        <button onClick={creationDeck}>creationDeck </button>
        <h1>carte nel mazzo {decks.length}</h1>
    </div>
  );
};


export default React.memo(PlayStrategy);
