import React, { FC, useEffect, useState } from 'react';

// style
import './ViewStrategyResult.scss';

interface ViewStrategyResultProps {
  resultStrategy:Array<string>
}

const ViewStrategyResult: FC<ViewStrategyResultProps> = ({resultStrategy}) => { 

  const [ textView, setTextView ] = useState('play')

  useEffect(()=>{ if(resultStrategy.length>0){
    setTextView('play')
  } else if(resultStrategy[0]===resultStrategy[1]){
    setTextView('right')
  } else setTextView('wrong')

  },[])

  return (
    <>   
      <div className="viewStrategyResult" data-testid="viewStrategyResult">
        {(textView==='play')&& <h1>PLAY THE GAME, TRY YOUR SKILLS </h1>  }
        {(textView==='wrong')&& <h1> WRONG,<br/> SORRY BAD DECISION! </h1>  }
        {(textView==='right')&& <h1> VERY GOOD,<br/> TOU ARE IN THE RIGHT WAY...  </h1>  }
        ViewStrategyResult Component
      </div>
    </>
  );
};

export default React.memo(ViewStrategyResult);
