import React, { FC, useState, useEffect } from 'react';

// style
import './counter.scss';

interface CounterProps {
  valueCard:number,
  deckCards:number
}

const Counter: FC<CounterProps> = ({ valueCard, deckCards }) => {

  const [ counter, setCounter ] = useState(0);
  const [ oldDeck, setOldDeck ] = useState(0);

  useEffect( () => setCounter( counter => counter + valueCard ) , 
    [valueCard, deckCards, oldDeck] 
  );

  useEffect( () => {
    if( oldDeck < deckCards ){
      setCounter(0)
      setOldDeck(deckCards)
    }
  }, 
    [ deckCards, oldDeck] 
  );

  return(
    <>
      <div className="counter" data-testid="counter">
        <h1>{counter}</h1> 
      </div>
    </>
  )
};

export default React.memo(Counter);
