import React, { FC, useState, useEffect } from 'react';

// style
import './counter.scss';

interface CounterProps {
  valueCard:number,
  newDeck:boolean
}

const Counter: FC<CounterProps> = ({ valueCard, newDeck }) => {

  const [ counter, setCounter ] = useState(0); 

  useEffect( () => setCounter( counter => (newDeck)?counter + valueCard:0 ) , 
    [valueCard, newDeck ] 
  );

  return(
    <>
      <div className="counter" data-testid="counter">
        <h1>{counter}</h1> 
      </div>
    </>
  )
};

export default React.memo(Counter);
