import { createSlice, PayloadAction } from '@reduxjs/toolkit'
import { deck, cardState } from "./deck";



const decks:Array<cardState>=[]

const decksStoreSlice = createSlice({
  name:'decks',
  initialState: decks,
  reducers:{
    setViewState:(state) => { console.log(state)  },
    setNumberDecks:(state, action:PayloadAction<number> ) => {
      for( let i:number=0; i<action.payload; i++){
        state.push( ...deck )
      }
    },
    // decrement:(state) => { state.count -= 1  },
    // incrementByPayload : (state, action:PayloadAction<number> ) => { state.count += action.payload  }, 
  }
})

export const getRandomInt = (max:number) => {
  return Math.floor(Math.random() * max);
}





export const { setViewState, setNumberDecks } = decksStoreSlice.actions 

export default decksStoreSlice.reducer
